import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/widgets.dart';class HomeView extends StatefulWidget {  const HomeView({super.key});  @override  State<HomeView> createState() => _HomeViewState();}class _HomeViewState extends State<HomeView> {  int marks = 0;  String grade = '';  void calculateGrade() {    setState(      () {        if (marks >= 91 && marks <= 100) {          grade = 'A1';        } else if (marks >= 81 && marks <= 90) {          grade = 'A2';        } else if (marks >= 71 && marks <= 80) {          grade = 'B1';        } else if (marks >= 61 && marks <= 70) {          grade = 'B2';        } else if (marks >= 51 && marks <= 60) {          grade = 'C1';        } else if (marks >= 41 && marks <= 50) {          grade = 'C2';        } else if (marks >= 33 && marks <= 40) {          grade = 'D1';        } else {          grade = 'Fail';        }      },    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.blue.shade100,      /*appBar: AppBar(        centerTitle: true,        title: Text('Grade Calculator'),      ),*/      body: SingleChildScrollView(        child: Center(          child: Column(            children: [              Container(                height: 853,                child: Stack(                  children: [                    Positioned(                      child: Container(                        margin: EdgeInsets.only(left: 35, right: 35, top: 40),                        child: Row(                          mainAxisAlignment: MainAxisAlignment.spaceBetween,                          children: <Widget>[                            CircleAvatar(                              backgroundColor: Colors.white,                              child: Icon(CupertinoIcons.signature),                            ),                            Text(                              'Nikhil',                              style: TextStyle(                                fontFamily: "PoppinsL",                                fontSize: 18,                              ),                            ),                            CircleAvatar(                              backgroundColor: Colors.white,                              child: Icon(CupertinoIcons.arrow_turn_up_right),                            ),                          ],                        ),                      ),                    ),                    Positioned(                      top: 150,                      left: 120,                      child: Text(                        'Grade Calculator',                        style: TextStyle(                          fontFamily: "PoppinsL",                          fontSize: 21,                        ),                      ),                    ),                    Positioned(                      top: 300,                      child: Container(                        width: 383,                        height: 580,                        decoration: BoxDecoration(                          color: Colors.white,                          borderRadius: BorderRadius.only(                            topLeft: Radius.circular(30),                            topRight: Radius.circular(30),                          ),                        ),                        child: Container(                          margin: EdgeInsets.only(top: 20),                          child: Column(                            children: <Widget>[                              Padding(                                padding: const EdgeInsets.only(top: 30),                                child: Text(                                  'Enter your marks',                                  style: TextStyle(                                    fontFamily: "PoppinsL",                                    fontSize: 18,                                  ),                                ),                              ),                              Container(                                margin: EdgeInsets.only(                                    left: 35, right: 35, top: 30),                                child: Row(                                  mainAxisAlignment:                                      MainAxisAlignment.spaceBetween,                                  children: <Widget>[                                    Container(                                      child: TextFormField(                                        keyboardType: TextInputType.number,                                        decoration: InputDecoration(                                          hintText: 'Marks',                                          hintStyle: TextStyle(                                            fontFamily: "PoppinsL",                                            fontSize: 14,                                          ),                                          border: UnderlineInputBorder(),                                        ),                                        onChanged: (value) {                                          marks = int.tryParse(value) ?? 0;                                        },                                      ),                                      width: 240,                                    ),                                    InkWell(                                      onTap: calculateGrade,                                      child: Container(                                        width: 50,                                        height: 50,                                        decoration: BoxDecoration(                                          borderRadius:                                              BorderRadius.circular(10),                                          color: Colors.black,                                        ),                                        child: Icon(                                          CupertinoIcons.share,                                          color: Colors.white,                                        ),                                      ),                                    ),                                  ],                                ),                              ),                              SizedBox(height: 60),                              Container(                                alignment: Alignment.center,                                width: 150,                                height: 40,                                decoration: BoxDecoration(                                  borderRadius: BorderRadius.circular(10),                                  color: Colors.grey.shade100,                                ),                                child: Text(                                  'Grade: $grade',                                  style: TextStyle(                                    fontFamily: "PoppinsL",                                  ),                                ),                              ),                            ],                          ),                        ),                      ),                    ),                  ],                ),              ),            ],          ),        ),      ),    );  }}